targets.expr:
quantile_over_time(
  0.9,
  (
    # — your existing deployment workloads —
    sum by (workload) (
      container_memory_working_set_bytes{
        job="kubelet",
        metrics_path="/metrics/cadvisor",
        namespace="sensei",
        container!="",
        image!=""
      }
      * on(namespace,pod)
        group_left(workload)
        namespace_workload_pod:kube_pod_owner:relabel{
          namespace="sensei",
          workload_type="deployment"
        }
    )
    or
    # — plus those three postgres pods, labelled as “workload” —
    label_replace(
      sum by (pod) (
        container_memory_working_set_bytes{
          job="kubelet",
          metrics_path="/metrics/cadvisor",
          namespace="database",
          pod=~"postgres16vector-(5|6|7)",
          container!="",
          image!=""
        }
      ),
      "workload", "$1", "pod", "(.*)"
    )
  )[7d:5m]
)

